import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {

  const [movieName, setMovieName] = useState('');
  const [movieReview, setMovieReview] = useState('');

// useEffect(() =>{
//     axios.get("http://127.0.0.1:3001/")
//     .then((response) => {
//       console.log(response.data);
//       console.log(" aqui")
//     })
//   });

  const submitReview = () => {
    Axios.post("http://127.0.0.1:3001/insert", {
      movieName: movieName,
      movieReview: movieReview
    }).then(() => {
      alert("Sudsfdsfdsfsdccess");
    }).catch(error => {
      console.error("Error:", error);
      alert("Failed to submit the review");
    });
  };


  // useEffect(() =>{
  //   axios.get("http://127.0.0.1:3001/select")
  //   .then((response) => {
  //     console.log(response.data);
  //     console.log(" aqui")
  //   })
  // });



  return (
    <div className="App">
      <h1>Hello World! This is gonna be a Boat Game!</h1>

      <div className="d-flex justify-content-center">
        <div className='col-6 d-flex flex-column'>
          <label className="text-center">Movie Name</label>
          <input type='text' name='movieName' className="mt-1" onChange={(e) => {
            setMovieName(e.target.value)
          }}/>
          <label className="text-center mt-3" >Movie Review</label>
          <input type='text' name='movieReview' className="mt-1" onChange={(e) => {
            setMovieReview(e.target.value)
          }}></input>
          <button className="text-center mt-3" onClick={submitReview}>Submit</button>
        </div>
        <div>

        </div>
      </div>
    </div>
  );
}

export default App;
