import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {

const submitReview = () => {
  Axios.post("http://localhost:3001/api/insert", {
    movieName: movieName,
    movieReview: movieReview
  }).then(() => {
    alert("Success");
  });
}

const [movieName, setMovieName] = useState('');
const [movieReview, setMovieReview] = useState('');

  return (
    <div className="App">
      <h1>Hello World! This is gonna be a Boat Game!</h1>

      <div className="d-flex justify-content-center">
        <div className='col-6 d-flex flex-column'>
          <label class="text-center">Movie Name</label>
          <input type='text' name='movieName' class="mt-1" onChange={(e) => {
            setMovieName(e.target.value)
          }}/>
          <label class="text-center mt-3" >Movie Review</label>
          <input type='text' name='movieReview' class="mt-1" onChange={(e) => {
            setMovieReview(e.target.value)
          }}></input>
          <button class="text-center mt-3" onClick={submitReview}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default App;
