import './App.css';
import React, {useRef, useEffect, useState} from "react";
import Axios from 'axios';

function App() {

  const movieNameRef = useRef(null);
  const movieReviewRef = useRef(null);
  const [users, setUsers] = useState([]);




  function submitReview (){

    Axios.post("http://127.0.0.1:3001/userroutes/insert", {
      firstName: movieNameRef.current.value,
      age: movieReviewRef.current.value
    }).then(() => {
      alert("Success");
    }).catch(error => {
      console.error("Error:", error);
      alert("Failed to submit the review");
    });
  };

  const getUsers = async () => {
      Axios.get("http://127.0.0.1:3001/userroutes/select").then((response) => {

      })
  };

  useEffect(() => {
    // Call getUsers when the component mounts
    getUsers();
  }, []); 

  return (
    <div className="App">
      <h1>Hello World! This is gonna be a Boat Game!</h1>

      <div className="d-flex justify-content-center">
        <div className='col-6 d-flex flex-column'>
          <label className="text-center">Movie Name</label>
          <input
            type='text'
            name='movieName'
            className="mt-1"
            ref={movieNameRef}
          />
          <label className="text-center mt-3">Movie Review</label>
          <input
            type='text'
            name='movieReview'
            className="mt-1"
            ref={movieReviewRef}
          />
          <button className="text-center mt-3" onClick={submitReview}>
            Submit
          </button>
        </div>
      </div>
      <div>
      <h1>User List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Age</th>
            {/* Add more table headers as needed */}
          </tr>
        </thead>
        <tbody>
        {users && users.length > 0 ? (
              users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.firstName}</td>
                  <td>{user.age}</td>
                  {/* Add more table data cells as needed */}
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3">No users found</td>
                {/* Adjust colSpan based on the number of columns in your table */}
              </tr>
            )}
        </tbody>
      </table>
    </div>
    </div>
  );
};

export default App;



