import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';


function App() {

  const movieNameRef = useRef(null);
  const movieReviewRef = useRef(null);

  const submitReview = () => {
    const name = movieNameRef.current.value;
    const review = movieReviewRef.current.value;

    Axios.post("http://127.0.0.1:3001/insert", {
      firstName: name,
      age: review
    }).then(() => {
      alert("Success");
    }).catch(error => {
      console.error("Error:", error);
      alert("Failed to submit the review");
    });
  };

  return (
    <div className="App">
      <h1>Hello World! This is gonna be a Boat Game!</h1>

      <div className="d-flex justify-content-center">
        <div className='col-6 d-flex flex-column'>
          <label className="text-center">Movie Name</label>
          <input
            type='text'
            name='movieName'
            className="mt-1"
            ref={movieNameRef}
          />
          <label className="text-center mt-3">Movie Review</label>
          <input
            type='text'
            name='movieReview'
            className="mt-1"
            ref={movieReviewRef}
          />
          <button className="text-center mt-3" onClick={submitReview}>
            Submit
          </button>
        </div>
        <div></div>
      </div>
    </div>
  );
};

export default App;



